import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

import PlaySwitch from '../components/PlaySwitch'
import Distortion from '../components/effects/Distortion'
import FeedbackDelay from '../components/effects/FeedbackDelay'
import AutoFilter from '../components/effects/AutoFilter'
import AutoPanner from '../components/effects/AutoPanner'
import AutoWah from '../components/effects/AutoWah'
import BitCrusher from '../components/effects/BitCrusher'
import Chebyshev from '../components/effects/Chebyshev'
import Chorus from '../components/effects/Chorus'
import Convolver from '../components/effects/Convolver'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 10,
      type: 'triangle',
      depth: 8,
      baseFrequency: 100,
      octaves: 2.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    let distortion = new Tone.Distortion({
      distortion: 0,
      oversample: '4x'
    })

    let feedbackDelay = new Tone.FeedbackDelay({
      delayTime: '4n',
      maxDelay: 0.8
    })

    let feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    let freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    let jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 1.5,
      preDelay: 0.01
    })

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 0.5,
      spread: 180
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    autoFilter.wet.value = 0
    autoPanner.wet.value = 0
    autoWah.wet.value = 0
    bitCrusher.wet.value = 0
    chebyshev.wet.value = 0
    chorus.wet.value = 0
    convolver.wet.value = 0
    distortion.wet.value = 0
    feedbackDelay.wet.value = 0
    feedbackEffect.wet.value = 0
    freeverb.wet.value = 0
    jcReverb.wet.value = 0
    phaser.wet.value = 0
    pingPongDelay.wet.value = 0
    pitchShift.wet.value = 0
    reverb.wet.value = 0
    stereoWidener.wet.value = 0
    tremolo.wet.value = 0
    vibrato.wet.value = 0

    // SYNTH
    let synth = new Tone.PolySynth()

    let synth1 = new Tone.Synth()
    let synth2 = new Tone.Synth()
    let synth3 = new Tone.Synth()
    let synth4 = new Tone.Synth()

    synth.chain(
      autoFilter,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      distortion,
      feedbackDelay,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      tremolo,
      vibrato,
      Tone.Master
    )

    // Loop
    let loop1 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    let loop2 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('E2', '32n', time)
    }, '32n')

    let loop3 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('D2', '1n', time)
    }, '1n')

    let loop4 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('A2', '16n', time)
    }, '16n')

    this.state = {
      lastChange: Date.now(),
      timeout: 100,
      autoFilter: {
        effect: autoFilter,
        wet: 0,
        on: false
      },
      autoPanner: {
        effect: autoPanner,
        wet: 0,
        on: false
      },
      autoWah: {
        effect: autoWah,
        wet: 0,
        on: false
      },
      bitCrusher: {
        effect: bitCrusher,
        wet: 0,
        on: false
      },
      chebyshev: {
        effect: chebyshev,
        wet: 0,
        on: false
      },
      chorus: {
        effect: chorus,
        wet: 0,
        on: false
      },
      convolver: {
        effect: convolver,
        wet: 0,
        on: false
      },
      distortion: {
        effect: distortion,
        wet: 0,
        on: false
      },
      feedbackDelay: {
        effect: feedbackDelay,
        wet: 0,
        on: false
      },
      feedbackEffect: {
        effect: feedbackEffect,
        wet: 0,
        on: false
      },
      freeverb: {
        effect: freeverb,
        wet: 0,
        on: false
      },
      jcReverb: {
        effect: jcReverb,
        wet: 0,
        on: false
      },
      phaser: {
        effect: phaser,
        wet: 0,
        on: false
      },
      pingPongDelay: {
        effect: pingPongDelay,
        wet: 0,
        on: false
      },
      pitchShift: {
        effect: pitchShift,
        wet: 0,
        on: false
      },
      reverb: {
        effect: reverb,
        wet: 0,
        on: false
      },
      stereoWidener: {
        effect: stereoWidener,
        wet: 0,
        on: false
      },
      tremolo: {
        effect: tremolo,
        wet: 0,
        on: false
      },
      vibrato: {
        effect: vibrato,
        wet: 0,
        on: false
      },
      synth: {
        instrument: synth,
        on: false
      },
      loop1: {
        loop: loop1,
        on: false
      },
      loop2: {
        loop: loop2,
        on: false
      },
      loop3: {
        loop: loop3,
        on: false
      },
      loop4: {
        loop: loop4,
        on: false
      }
    }

    _.bindAll(
      this,
      'getRandomArbitrary',
      'generateRandom',
      'toggleLoop',
      'toggleEffect',
      'changeEffectWetValue',
      'changeDistortionValue',
      'changeAutoFilterValue',
      'changeAutoFilterWetValue',
      'changeAutoPannerValue',
      'changeAutoPannerWetValue',
      'changeAutoWahValue',
      'changeAutoWahWetValue',
      'changeBitCrusherValue',
      'changeBitCrusherWetValue',
      'changeChebyshevValue',
      'changeChebyshevWetValue',
      'changeChorusValue',
      'changeChorusWetValue',
      'changeConvolverValue',
      'changeConvolverWetValue',
      'changeFeedbackDelayValue'
    )

    Tone.Transport.bpm.value = 30
    Tone.Transport.start()
  }

  componentDidMount() {
    this.generateRandom()
  }

  getRandomArbitrary(min, max) {
    return Math.floor(Math.random() * (max - min)) + min
  }

  generateRandom() {
    const { lastChange, timeout } = this.state

    if (Date.now() - lastChange >= timeout) {
      const random = this.getRandomArbitrary(100, 3000)

      this.setState({
        lastChange: Date.now(),
        timeout: random
      })

      this.changeDistortionValue('distortion', random / 30)
    }

    setTimeout(() => this.generateRandom(), timeout)
  }

  toggleLoop(loopName) {
    let { loop, on } = this.state[loopName]

    on == true ? loop.stop() : loop.start('0m')

    this.setState({
      [`${loopName}`]: {
        loop: loop,
        on: !on
      }
    })
  }

  toggleEffect(effectName) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeEffectWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeDistortionValue(effectName, value) {
    let { effect, wet, on } = this.state.distortion

    effect.distortion = value

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoFilterValue(effectName, value) {
    let { effect, wet, on } = this.state.autoFilter

    effect.autoFilter = value

    this.setState({
      autoFilter: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoFilterWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoPannerValue(effectName, value) {
    let { effect, wet, on } = this.state.autoPanner

    effect.autoPanner = value

    this.setState({
      autoPanner: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoPannerWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoWahValue(effectName, value) {
    let { effect, wet, on } = this.state.autoWah

    effect.autoWah = value

    this.setState({
      autoWah: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoWahWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeBitCrusherValue(effectName, value) {
    let { effect, wet, on } = this.state.bitCrusher

    effect.bitCrusher = value

    this.setState({
      bitCrusher: {
        effect,
        wet,
        on
      }
    })
  }

  changeBitCrusherWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeChebyshevValue(effectName, value) {
    let { effect, wet, on } = this.state.chebyshev

    effect.chebyshev = value

    this.setState({
      chebyshev: {
        effect,
        wet,
        on
      }
    })
  }

  changeChebyshevWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeChorusValue(effectName, value) {
    let { effect, wet, on } = this.state.chorus

    effect.chorus = value

    this.setState({
      chorus: {
        effect,
        wet,
        on
      }
    })
  }

  changeChorusWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeConvolverValue(effectName, value) {
    let { effect, wet, on } = this.state.convolver

    effect.convolver = value

    this.setState({
      convolver: {
        effect,
        wet,
        on
      }
    })
  }

  changeConvolverWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeFeedbackDelayValue(effectName, value) {
    let { effect, wet, on } = this.state.feedbackDelay

    effect.maxDelay = value

    this.setState({
      feedbackDelay: {
        effect,
        wet,
        on
      }
    })
  }

  render() {
    let {
      distortion,
      autoFilter,
      feedbackDelay,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,

      synth,
      loop1,
      loop2,
      loop3,
      loop4
    } = this.state
    let { toggleEffect } = this

    return (
      <div>
        Toggle Loop 1
        <PlaySwitch
          name="play"
          value={loop1.on}
          handleToggleClick={() => this.toggleLoop('loop1')}
        />
        Toggle Loop 2
        <PlaySwitch
          name="play"
          value={loop2.on}
          handleToggleClick={() => this.toggleLoop('loop2')}
        />
        Toggle Loop 3
        <PlaySwitch
          name="play"
          value={loop3.on}
          handleToggleClick={() => this.toggleLoop('loop3')}
        />
        Toggle Loop 4
        <PlaySwitch
          name="play"
          value={loop4.on}
          handleToggleClick={() => this.toggleLoop('loop4')}
        />
        <div className="effectsBoard">
          <div className="DistortionSynth1">
            <Distortion
              {...this.state.distortion}
              toggleEffect={() => toggleEffect('distortion')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeDistortionValue={this.changeDistortionValue}
            />
          </div>
          <div className="DistortionSynth2">
            <FeedbackDelay
              {...this.state.feedbackDelay}
              toggleEffect={() => toggleEffect('feedbackDelay')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeFeedbackDelayValue={this.changeFeedbackDelayValue}
            />
          </div>
          <div className="DistortionSynth3">
            <AutoFilter
              {...this.state.autoFilter}
              toggleEffect={() => toggleEffect('autoFilter')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeAutoFilterValue={this.changeAutoFilterValue}
            />
          </div>
          <div className="DistortionSynth4">
            <AutoPanner
              {...this.state.autoPanner}
              toggleEffect={() => toggleEffect('autoPanner')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeAutoPannerValue={this.changeAutoPannerValue}
            />
          </div>
        </div>
        <div className="effectsBoard2">
          <div className="DistortionSynth5">
            <AutoWah
              {...this.state.autoWah}
              toggleEffect={() => toggleEffect('autoWah')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeAutoWahValue={this.changeAutoWahValue}
            />
          </div>
          <div className="DistortionSynth6">
            <BitCrusher
              {...this.state.bitCrusher}
              toggleEffect={() => toggleEffect('bitCrusher')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeBitCrusherValue={this.changeBitCrusherValue}
            />
          </div>
          <div className="DistortionSynth7">
            <Chebyshev
              {...this.state.chebyshev}
              toggleEffect={() => toggleEffect('chebyshev')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeChebyshevValue={this.changeChebyshevValue}
            />
          </div>
          <div className="DistortionSynth8">
            <Chorus
              {...this.state.chorus}
              toggleEffect={() => toggleEffect('chorus')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeChorusValue={this.changeChorusValue}
            />
          </div>
          <div className="DistortionSynth9">
            <Convolver
              {...this.state.convolver}
              toggleEffect={() => toggleEffect('convolver')}
              changeEffectWetValue={this.changeEffectWetValue}
              changeConvolverValue={this.changeConvolverValue}
            />
          </div>
        </div>
      </div>
    )
  }
}
