import _ from 'lodash'
import 'whatwg-fetch'
import React from 'react'
import Tone from 'tone'

import * as effects from '../tunes/effects'
import * as parts from '../tunes/parts'
import * as synths from '../tunes/synths'

import Distortion from '../components/effects/Distortion'
import FeedbackDelay from '../components/effects/FeedbackDelay'
import AutoPanner from '../components/effects/AutoPanner'
import AutoWah from '../components/effects/AutoWah'
import BitCrusher from '../components/effects/BitCrusher'
import Chebyshev from '../components/effects/Chebyshev'
import Chorus from '../components/effects/Chorus'
import Freeverb from '../components/effects/Freeverb'
import JCReverb from '../components/effects/JCReverb'
import Phaser from '../components/effects/Phaser'
import PingPongDelay from '../components/effects/PingPongDelay'
import PitchShift from '../components/effects/PitchShift'
import Tremolo from '../components/effects/Tremolo'
import Vibrato from '../components/effects/Vibrato'
import StereoWidener from '../components/effects/StereoWidener'

import ToneSynth from '../components/synths/ToneSynth'
import PolySynth from '../components/synths/PolySynth'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    const defaultWetValue = 0.8

    let ambientMachine = {
      synth: toneSynth(),
      chorus: {
        effect: effects.chorus(),
        wet: defaultWetValue,
        on: false
      },
      distortion: {
        effect: effects.distortion(),
        wet: defaultWetValue,
        on: false
      },
      feedbackDelay: {
        effect: effects.feedbackDelay(),
        wet: defaultWetValue,
        on: false
      },
      freeverb: {
        effect: effects.freeverb(),
        wet: defaultWetValue,
        on: false
      }
    }

    let leadMachine = {
      synth: polySynth(),
      distortion: {
        effect: effects.distortion(),
        wet: defaultWetValue,
        on: false
      }
    }

    ambientMachine.synth.chain(
      ambientMachine.chorus,
      ambientMachine.distortion,
      ambientMachine.feedbackDelay,
      ambientMachine.freeverb,
      Tone.Master
    )

    leadMachine.synth.chain(leadMachine.distortion, Tone.Master)

    // noiseSynth.chain(Tone.Master)

    // Loop
    let loop1 = new Tone.Loop(function(time) {
      ambientMachine.synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    let loop2 = new Tone.Loop(function(time) {
      ambientMachine.synth.triggerAttackRelease('E2', '32n', time)
    }, '32n')

    let loop3 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('D2', '1n', time)
    }, '1n')

    let loop4 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('A2', '16n', time)
    }, '16n')

    let loop5 = new Tone.Loop(function(time) {
      ambientMachine.synth.triggerAttackRelease('C4', '1m', time)
    }, '1m')

    let loop6 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('G0', '8n', time)
    }, '16n')

    let loop7 = new Tone.Loop(function(time) {
      ambientMachine.synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    let loop8 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('G0', '2n', time)
    }, '8n')

    let loop9 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('A1', '16n', time)
    }, '32n')

    this.state = {
      lastChange: Date.now(),
      timeout: 100,
      instruments: {
        ambientMachine,
        leadMachine
      },
      loop1: {
        loop: loop1,
        on: false
      },
      loop2: {
        loop: loop2,
        on: false
      },
      loop3: {
        loop: loop3,
        on: false
      },
      loop4: {
        loop: loop4,
        on: false
      },
      loop5: {
        loop: loop5,
        on: false
      },
      loop6: {
        loop: loop6,
        on: false
