import _ from "lodash";
import React from "react";
import Tone from "tone";

import PlaySwitch from "../components/PlaySwitch";
import ToggleSwitch from "../components/ToggleSwitch";
import Slider from "../components/Slider";
import Knob from "../components/Knob";

export default class Synth extends React.Component {
  constructor(props) {
    super(props);

    let distortion = new Tone.Distortion({
      distortion: 0,
      oversample: "4x"
    });

    let autoPanner = new Tone.AutoFilter({
      frequency: 10,
      type: "triangle",
      depth: 8,
      baseFrequency: 100,
      octaves: 2.6,
      filter: {
        type: "lowpass",
        rolloff: -12,
        Q: 1
      }
    });

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    });

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    });

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease("C2", "8n", time);
    }, "4n");

    distortion.wet.value = 0;

    let synth = new Tone.Synth();
    synth.chain(distortion, autoPanner, autoWah, bitCrusher, Tone.Master);

    this.state = {
      distortion: { effect: distortion, wet: 0, on: false },
      synth: { instrument: synth, on: false },
      autoPanner: { effect: autoPanner, wet: 0, on: false },
      autoWah: { effect: autoWah, wet: 0, on: false },
      bitCrusher: { effect: bitCrusher, wet: 0, on: false },
      loop
    };

    _.bindAll(
      this,
      "toggleSynth",
      "toggleDistortion",
      "changeDistortionWetValue",
      "changeDistortionValue",
      "toggleautoPanner",
      "changeautoPannerValue",
      "toggleautoWah",
      "changeautoWahValue",
      "togglebitCrusher",
      "changebitCrusherValue"
    );
  }

  toggleSynth() {
    let { synth, loop } = this.state;
    let { instrument, on } = synth;

    on == true ? loop.stop() : loop.start("0m");

    this.setState({
      synth: {
        instrument: instrument,
        on: !on
      }
    });

    Tone.Transport.bpm.value = 115;
    Tone.Transport.start();
  }

  toggleDistortion() {
    let { effect, wet, on } = this.state.distortion;

    effect.wet.value = on == true ? 0 : wet;
    on = !on;

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    });
  }

  toggleautoPanner() {
    let { effect, on } = this.state.autoPanner;

    this.setState({
      autoPanner: {
        effect,
        on
      }
    });
  }

  togglebitCrusher() {
    let { effect, on } = this.state.bitCrusher;

    this.setState({
      bitCrusher: {
        effect,
        on
      }
    });
  }

  toggleautoWah() {
    let { effect, wet, on } = this.state.autoWah;

    this.setState({
      autoWah: {
        effect,
        wet,
        on
      }
    });
  }

  changeDistortionWetValue(value) {
    let { effect, wet, on } = this.state.distortion;

    effect.wet.value = on == true ? value : 0;
    wet = value;

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    });
  }

  changeDistortionValue(value) {
    let { effect, wet, on } = this.state.distortion;

    effect.distortion = value;

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    });
  }

  changeautoWahValue(value) {
    let { effect, wet, on } = this.state.autoWah;

    effect.autoWah = value;

    this.setState({
      autoWah: {
        effect,
        wet,
        on
      }
    });
  }

  changeautoPannerValue(value) {
    let { effect, wet, on } = this.state.autoPanner;

    effect.autoPanner = value;

    this.setState({
      autoPanner: {
        effect,
        wet,
        on
      }
    });
  }

  changebitCrusherValue(value) {
    let { effect, on } = this.state.bitCrusher;

    effect.bitCrusher = value;

    this.setState({
      bitCrusher: {
        effect,
        on
      }
    });
  }

  render() {
    let { distortion, synth, autoPanner, autoWah, bitCrusher } = this.state;
    return (
      <div>
        Toggle Synth
        <PlaySwitch
          name="play"
          value={synth.on}
          handleToggleClick={this.toggleSynth}
        />
        Change Distortion Wet Value
        <Slider
          min="0"
          max="1"
          value={distortion.effect.wet.value}
          handleValueChange={this.changeDistortionWetValue}
        />
        Bit Crusher
        <Slider
          min="0"
          max="1"
          value={bitCrusher.effect.bitCrusher}
          handleValueChange={this.changebitCrusherValue}
        />
        Auto Panner
        <Slider
          min="0"
          max="100"
          value={autoPanner.effect.autoPanner}
          handleValueChange={this.changeautoPannerValue}
        />
        Auto Wah
        <Slider
          min="0"
          max="100"
          value={autoWah.effect.autoWah}
          handleValueChange={this.changeautoWahValue}
        />
        Change Distortion Value
        <Slider
          min="0"
          max="100"
          value={distortion.effect.distortion}
          handleValueChange={this.changeDistortionValue}
        />
        Knob
        <Knob min="" max="" handleValueChange={this.changeDistortionValue} />
        ToggleDistortion
        <ToggleSwitch
          value="Distortion"
          current={distortion.on}
          handleClick={this.toggleDistortion}
        />
      </div>
    );
  }
}
