import React from "react";

import PlaySwitch from "../PlaySwitch";
import ToggleSwitch from "../ToggleSwitch";
import Slider from "../Slider";
import Knob from "../Knob";

export default class FeedbackDelay extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let name = "distortion";
    const {
      effect,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeDistortionValue
    } = this.props;

    return (
      <div>
        <h1>FeedbackDelay</h1>
        <Slider
          name={name}
          min="0"
          max="1"
          value={effect.wet.value}
          handleValueChange={changeEffectWetValue}
        />
        Change Distortion Value
        <Slider
          name={name}
          min="0"
          max="100"
          value={effect.distortion}
          handleValueChange={changeDistortionValue}
        />
        Toggle Distortion
        <ToggleSwitch
          value="Distortion"
          current={on}
          handleClick={toggleEffect}
        />
        Bit Crusher
        <Slider
          min="1"
          max="10"
          value={bitCrusher.effect.bitCrusher}
          handleValueChange={this.changebitCrusherValue}
        />
        Change Bit Crusher Wet Value
        <Slider
          min="0"
          max="10"
          value={bitCrusher.effect.wet.value}
          handleValueChange={this.changebitCrusherWetValue}
        />
        <ToggleSwitch
          value="Bit Crusher"
          current={bitCrusher.on}
          handleClick={this.toggleBitCrusher}
        />
        Auto Panner
        <Slider
          min="0"
          max="100"
          value={autoPanner.effect.autoPanner}
          handleValueChange={this.changeautoPannerValue}
        />
        Change Auto Panner Wet Value
        <Slider
          min="0"
          max="10"
          value={autoPanner.effect.wet.value}
          handleValueChange={this.changeautoPannerWetValue}
        />
        <ToggleSwitch
          value="AutoPanner"
          current={autoPanner.on}
          handleClick={this.toggleAutoPanner}
        />
        Auto Wah
        <Slider
          min="0"
          max="100"
          value={autoWah.effect.autoWah}
          handleValueChange={this.changeautoWahValue}
        />
        Change Auto Wah Wet Value
        <Slider
          min="0"
          max="8"
          value={autoWah.effect.wet.value}
          handleValueChange={this.changeautoWahWetValue}
        />
        <ToggleSwitch
          value="Auto Wah"
          current={autoWah.on}
          handleClick={this.toggleAutoWah}
        />
        Chorus
        <Slider
          min="1.5"
          max="10"
          value={chorus.effect.chorus}
          handleValueChange={this.changechorusValue}
        />
        Change Chorus Wet Value
        <Slider
          min="0"
          max="5"
          value={chorus.effect.wet.value}
          handleValueChange={this.changechorusWetValue}
        />
        <ToggleSwitch
          value="Chorus"
          current={chorus.on}
          handleClick={this.toggleChorus}
        />
        Freeverb
        <Slider
          min="1.5"
          max="10"
          value={chorus.effect.chorus}
          handleValueChange={this.changechorusValue}
        />
        Change Freeverb Wet Value
        <Slider
          min="0"
          max="5"
          value={freeverb.effect.wet.value}
          handleValueChange={this.changefreeverbWetValue}
        />
        <ToggleSwitch
          value="Freeverb"
          current={freeverb.on}
          handleClick={this.toggleFreeverb}
        />
      </div>
    );
  }
}
