import React from "react";
import Tone from "tone";

export default class Synth extends React.Component {
  constructor(props) {
    super(props);

    let distortion = new Tone.Distortion({
      distortion: 100,
      oversample: "4x"
    });

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: "sine",
      depth: 0.5,
      spread: 180
    });

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: "sine",
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: "lowpass",
        rolloff: -12,
        Q: 1
      }
    });

    let feedbackDelay1 = new Tone.FeedbackDelay({
      delayTime: 0.25,
      maxDelay: 1
    });

    let autoPanner = new Tone.AutoPanner({
      frequency: 1,
      type: "sine",
      depth: 1
    });

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    });

    this.state = {
      tremolo: tremolo,
      tremoloIsOn: false,
      distortion: distortion,
      distortionIsOn: false,
      autoFilter: autoFilter,
      autoFilterIsOn: false,
      feedbackDelay1: feedbackDelay1,
      feedbackDelay1: false,
      autoPanner: autoPanner,
      autoPannerIsOn: false,
      autoWah: autoWah,
      autoWahIsOn: false
    };

    this.startSynth = this.startSynth.bind(this);
    this.toggleFilter = this.toggleFilter.bind(this);
    this.toggleFeedbackDelay1 = this.toggleFeedbackDelay1.bind(this);
    this.toggleTremolo = this.toggleTremolo.bind(this);
    this.toggleDistortion = this.toggleDistortion.bind(this);
    this.toggleDistortion100 = this.toggleDistortion100.bind(this);
    this.toggleAutoPanner = this.toggleAutoPanner.bind(this);
    this.toggleAutoWah = this.toggleAutoWah.bind(this);
  }

  startSynth() {
    let synth = new Tone.Synth(); //.toMaster();
    synth.chain(
      this.state.distortion,
      this.state.autoFilter,
      this.state.feedbackDelay1,
      this.state.feedbackDelay2,
      this.state.tremolo,
      this.state.autoPanner,
      this.state.autoWah,
      Tone.Master
    );

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease("C2", "8n", time);
    }, "4n");

    loop.start("0m").stop("16m");

    Tone.Transport.bpm.value = 115;
    Tone.Transport.start();
  }

  toggleFilter() {
    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: "sine",
      depth: 0,
      baseFrequency: 200,
      octaves: 0.6,
      filter: {
        type: "lowpass",
        rolloff: -12,
        Q: 1
      }
    });

    this.setState({
      autoFilter: autoFilter
    });

    let { synth } = this.state;
    this.state.synth.connect(autoFilter);
  }

  toggleFeedbackDelay() {
    let feedbackDelay1 = new Tone.FeedbackDelay({
      delayTime: "32n",
      maxDelay: 10
    }).toMaster();

    let feedbackDelay2 = new Tone.FeedbackDelay({
      delayTime: "8n",
      maxDelay: 10
    }).toMaster();

    this.state.synth.connect(feedbackDelay1);
    this.state.synth.connect(feedbackDelay2);
  }

  toggleTremolo() {
    this.state.synth.connect(tremolo);
  }

  toggleDistortion() {
    console.log("toggle");

    if (this.state.distortionIsOn == true) {
      console.log("true");

      this.state.distortion.wet.value = 0;

      this.setState({
        distortionIsOn: false
      });
    } else {
      console.log("false");
      this.state.synth.chain(this.state.distortion, Tone.Master);
      this.state.distortion.wet.value = 1;

      this.setState({
        distortionIsOn: true
      });
    }
  }

  toggleAutoPanner() {
    console.log("autoPlay");

    if (this.state.autoPannerIsOn == true) {
      this.state.autoPanner.wet.value = 0;

      this.setState({
        autoPannerIsOn: false
      });
    } else {
      this.state.autoPanner.wet.value = 1;

      this.setState({
        autoPannerIsOn: true
      });
    }
  }

  toggleAutoWah() {
    if (this.state.autoWahIsOn == true) {
      this.state.autoWahIsOn.wet.value = 0;

      this.setState({
        autoWahIsOn: false
      });
    } else {
      this.state.autoWah.wet.value = 1;

      this.setState({
        autoWahIsOn: true
      });
    }
  }

  toggleDistortion100() {
    let { distortion } = this.state;
    distortion.distortion = 100;

    this.setState({
      distortion: distortion
    });
  }

  render() {
    return (
      <div>
        <div onClick={this.startSynth}>Start</div>
        <div onClick={this.toggleFilter}>Toggle Filter</div>
        <div onClick={this.toggleFeedbackDelay}>Toggle Feedback Delay</div>
        <div onClick={this.toggleTremolo}>Toggle Tremolo</div>
        <div onClick={this.toggleDistortion}>
          Toggle Distortion {this.state.distortionIsOn}
        </div>
        <div onClick={this.toggleDistortion100}>Toggle Distortion 100</div>
        <div onClick={this.toggleAutoPanner}>
          AutoPanner {this.state.autoPannerIsOn}
        </div>
        <div onClick={this.toggleAutoWah}>AutoWah {this.state.autoWahIsOn}</div>
      </div>
    );
  }
}
