import React from "react";
import Tone from "tone";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/instrument/Instrument.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/source/FMOscillator.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/component/Filter.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/component/FrequencyEnvelope.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/component/AmplitudeEnvelope.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/core/Gain.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/signal/Scale.js";
import "/Users/anastasiakazmina/hseadc2017_thereminvox2/Tone.js/Tone/signal/Multiply.js";

export default class Thereminvox extends React.Component {
  constructor(props) {
    super(props);

    const audioContext = new (window.AudioContext ||
      window.webkitAudioContext)();
    let oscillator = audioContext.createOscillator();
    oscillator.type = "sine";

    let analyser = audioContext.createAnalyser();
    analyser.fftsize = 2048;
    oscillator.connect(analyser);

    this.state = {
      audioContext: audioContext,
      oscillator: oscillator,
      analyser: analyser,
      playing: false,
      x: 0,
      y: 0,
      fftData: []
    };

    this.handleMouseMove = this.handleMouseMove.bind(this);
    this.handleStart = this.handleStart.bind(this);
    this.handleStop = this.handleStop.bind(this);
    this.handleStartOrStopClick = this.handleStartOrStopClick.bind(this);
    this.changeFrequency = this.changeFrequency.bind(this);
    this.changeDetune = this.changeDetune.bind(this);
    this.changeVisualization = this.changeVisualization.bind(this);
    this.handleSynthPlay = this.handleSynthPlay.bind(this);
    this.handleToneReverb = this.handleToneReverb.bind(this);
  }

  componentDidMount() {
    document.addEventListener("mousemove", this.handleMouseMove);
  }

  handleMouseMove(e) {
    this.setState({
      x: e.clientX,
      y: e.clientY
    });

    this.changeFrequency();
    this.changeDetune();
    this.changeVisualization();
  }

  handleStartOrStopClick() {
    let { playing } = this.state;

    if (playing) {
      this.handleStop();
    } else {
      this.handleStart();
    }
  }

  handleToneReverb() {
    class Button extends Component {
      render() {
        console.log("ToneReverb");

        Tone.Reverb = function() {
          var options = Tone.defaults(arguments, ["decay"], Tone.Reverb);
          Tone.Effect.call(this, options);

          this._convolver = this.context.createConvolver();

          this.decay = options.decay;

          this.preDelay = options.preDelay;

          this.connectEffect(this._convolver);
        };

        Tone.extend(Tone.Reverb, Tone.Effect);

        Tone.Reverb.defaults = {
          decay: 1.5,
          preDelay: 0.01
        };

        Tone.Reverb.prototype.generate = function() {
          return Tone.Offline(
            function() {
              //create a noise burst which decays over the duration
              var noiseL = new Tone.Noise();
              var noiseR = new Tone.Noise();
              var merge = new Tone.Merge();
              noiseL.connect(merge.left);
              noiseR.connect(merge.right);
              var gainNode = new Tone.Gain().toMaster();
              merge.connect(gainNode);
              noiseL.start(0);
              noiseR.start(0);
              //predelay
              gainNode.gain.setValueAtTime(0, 0);
              gainNode.gain.setValueAtTime(1, this.preDelay);
              //decay
              gainNode.gain.exponentialApproachValueAtTime(
                0,
                this.preDelay,
                this.decay + this.preDelay
              );
            }.bind(this),
            this.decay + this.preDelay
          ).then(
            function(buffer) {
              this._convolver.buffer = buffer.get();
              return this;
            }.bind(this)
          );
        };

        Tone.Reverb.prototype.dispose = function() {
          Tone.Effect.prototype.dispose.call(this);
          this._convolver.disconnect();
          this._convolver = null;
          return this;
        };
      }
    }
  }

  handleSynthPlay() {
    console.log("Yo");
    let synth = new Tone.Synth().toMaster();
    //synth.triggerAttackRelease("A4", "4n");

    //let pattern = new Tone.Pattern(
    //  function(time, note) {
    //    synth.triggerAttackRelease(note, "16n");
    //  },
    //  ["C4", "D4", "E4", "G4", "A4"]
    //);

    //  pattern.start(0);

    //  let synth = new Tone.Synth({
    //  oscillator: {
    //    type: "pwm",
    //    modulationFrequency: 0.2
    //  },
    //  envelope: {
    //    attack: 0.02,
    //    decay: 0.1,
    //    sustain: 0.2,
    //    release: 0.9
    //  }
    //  }).toMaster();

    //synth.triggerAttack("D3", "+1");

    //let polySynth = new Tone.PolySynth(4, Tone.Synth).toMaster();

    //let distortion = new Tone.Distortion(0.4).toMaster();
    //polySynth.triggerAttackRelease(["C4", "E4", "G4", "B4"], "2n");
    //synth.connect(distortion);

    //let pwm = new Tone.PWMOscillator("Bb3").toMaster().start();

    //  let note = new Tone.Event(function(time, pitch) {
    //    synth.triggerAttackRelease(pitch, "16n", time);
    //  }, "C2");

    //  Tone.Transport.scheduleRepeat(
    //    function(time) {
    //      synth.triggerAttack(time);
    //    },
    //    "8n",
    //    "1m"
    //  );

    //  note.set({
    //    loop: true,
    //    loopEnd: "2n"
    //  });

    //  note.start(0);
    //note.stop("4m");

    let part = new Tone.Part(
      function(time, pitch) {
        synth.triggerAttackRelease(pitch, "8n", time);
      },
      [["0", "C#3"], ["4n", "G3"], [3 * Tone.Time("8n"), "G#3"], ["2n", "C3"]]
    );

    //part.start("4m");

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease("C2", "8n", time);
    }, "4n");

    loop.start("1n"); //.stop("4m");

    Tone.Transport.bpm.value = 220;
    Tone.Transport.start();
  }

  handleStart() {
    let { audioContext, oscillator, analyser, x, y } = this.state;

    oscillator = audioContext.createOscillator();

    oscillator.type = "sine";
    oscillator.frequency.setValueAtTime(x, audioContext.currentTime);

    oscillator.connect(audioContext.destination);
    oscillator.start();

    analyser = audioContext.createAnalyser();
    analyser.fftsize = 2048;
    oscillator.connect(analyser);

    this.setState({
      oscillator: oscillator,
      analyser: analyser,
      playing: true
    });
  }

  handleStop() {
    let { oscillator } = this.state;
    oscillator.stop();

    this.setState({
      oscillator: oscillator,
      playing: false
    });
  }

  changeFrequency() {
    let { audioContext, oscillator, x, y } = this.state;
    oscillator.frequency.setValueAtTime(x, audioContext.currentTime);
  }

  changeDetune() {
    let { audioContext, oscillator, x, y } = this.state;
    oscillator.detune.setValueAtTime(y, audioContext.currentTime);
  }

  changeVisualization() {
    const { analyser, playing } = this.state;

    if (playing) {
      const bufferLenght = analyser.frequencyBinCount;
      let dataArray = new Uint8Array(bufferLenght);
      analyser.getByteTimeDomainData(dataArray);

      this.setState({
        fftData: dataArray
      });
    }
  }

  render() {
    const { playing, analyser, fftData } = this.state;
    let button = "Start";

    if (playing) {
      button = "Stop";
    }

    let elements = [];

    console.log(this.state);

    if (fftData != undefined) {
      fftData.map(function(fftParam, i) {
        elements.push(
          <div
            key={i}
            className="analyserCol"
            style={{ height: fftParam + "px" }}
          />
        );
      });
    }

    return (
      <div>
        <div onClick={this.handleSynthPlay}>Synth</div>
        <div onClick={this.handleMetalSynthPlay}>ToneReverb</div>
        <div onClick={this.handleStartOrStopClick}>{button}</div>
        <div className="analyser">{elements}</div>
      </div>
    );
  }
}
