import React from "react";

export default class Avatar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      color: "red"
    };

    this.handleClick = this.handleClick.bind(this);
  }

  ///

  handleClick() {
    console.log("Inner handle click");
    this.props.handleClick();

    this.setState({
      color: "blue"
    });

    window.onload = function() {
      document.body.addEventListener("mouseover", function(e) {
        if (!e.target.hasAttribute("data-tooltip")) return;
        let message = document.createElement("div"),
          targetPosition = e.target.getBoundingClientRect(),
          span = document.createElement("span"),
          top;
        message.classList.add("msg_box");
        span.innerHTML = e.target.getAttribute("data-tooltip");
        message.append(span);
        if (!document.querySelector(".msg_box"))
          document.body.appendChild(message, e.target);
        let selectMessage = document.querySelector(".msg_box");
        if (targetPosition.top > selectMessage.offsetHeight) {
          top = targetPosition.top - selectMessage.offsetHeight - 5;
        } else {
          top = targetPosition.height + targetPosition.top + 5;
        }
        selectMessage.style.top = top + "px";
        selectMessage.style.left = targetPosition.left + "px";
      });
      document.body.addEventListener("mouseout", function(e) {
        if (!e.target.hasAttribute("data-tooltip")) return;
        if (document.querySelector(".msg_box"))
          document.querySelector(".msg_box").remove();
      });
    };

    document.onclick = function(event) {
      var target = event.target;

      var id = target.getAttribute("data-toggle-id");
      if (!id) return;

      var elem = document.getElementById(id);

      elem.hidden = !elem.hidden;
    };
  }

  ///

  render() {
    const { name, handleClick } = this.props;

    return (
      <div
        data-toggle-id
        onClick={this.handleClick}
        style={{ color: this.state.color }}
      >
        {name} {this.state.color}
      </div>
    );
  }
}
