import _ from "lodash";
import React from "react";
import Tone from "tone";

import PlaySwitch from "../components/PlaySwitch";
import Distortion from "../components/effects/Distortion";
import FeedbackDelay from "../components/effects/FeedbackDelay";
import Knob from "../components/Knob";

export default class Synth extends React.Component {
  constructor(props) {
    super(props);

    let distortion = new Tone.Distortion({
      distortion: 0,
      oversample: "4x"
    });

    let autoPanner = new Tone.AutoFilter({
      frequency: 10,
      type: "triangle",
      depth: 8,
      baseFrequency: 100,
      octaves: 2.6,
      filter: {
        type: "lowpass",
        rolloff: -12,
        Q: 1
      }
    });

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: "sine",
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: "lowpass",
        rolloff: -12,
        Q: 1
      }
    });

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    });

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: "sine",
      spread: 180
    });

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    });

    let freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    });

    let feedbackDelay = new Tone.FeedbackDelay({
      delayTime: "4n",
      maxDelay: 0.8
    });

    distortion.wet.value = 0;
    feedbackDelay.wet.value = 0;

    let synth = new Tone.PolySynth();

    synth.chain(
      autoFilter,
      distortion,
      autoPanner,
      autoWah,
      bitCrusher,
      chorus,
      freeverb,
      feedbackDelay,
      Tone.Master
    );

    let loop1 = new Tone.Pattenr(function(time) {
      synth.triggerAttackRelease(["D4", "C4", "D4", "F4"], "8n", time);
    }, "4n");

    let loop2 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease("E2", "32n", time);
    }, "32n");

    let loop3 = new Tone.Pattern(function(time) {
      synth.triggerAttackRelease("D2", "1n", time);
    }, "1n");

    let loop4 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease("A2", "16n", time);
    }, "16n");

    this.state = {
      lastChange: Date.now(),
      timeout: 0,
      distortion: { effect: distortion, wet: 0, on: false },
      synth: { instrument: synth, on: false },
      autoPanner: { effect: autoPanner, wet: 0, on: false },
      autoWah: { effect: autoWah, wet: 0, on: false },
      bitCrusher: { effect: bitCrusher, wet: 0, on: false },
      chorus: { effect: chorus, wet: 0, on: false, frequency: 1.5 },
      freeverb: { effect: freeverb, wet: 0, on: false, roomSize: 1 },
      feedbackDelay: { effect: feedbackDelay, wet: 0, on: false },
      loop1: { loop: loop1, on: false },
      loop2: { loop: loop2, on: false },
      loop3: { loop: loop3, on: false },
      loop4: { loop: loop3, on: false }
    };

    _.bindAll(
      this,
      "generateRandom",
      "toggleLoop",
      "toggleEffect",
      "changeEffectWetValue",
      "changeDistortionValue",
      "changeFeedbackDelayValue"
    );

    Tone.Transport.bpm.value = 30;
    Tone.Transport.start();
  }

  componentDidMount() {
    this.generateRandom();
  }

  getRandomArbitrary(min, max) {
    return Math.floor(Math.random() * (max - min)) + min;
  }

  generateRandom() {
    const { lastChange, timeout } = this.state;

    if (Date.now() - lastChange >= timeout) {
      const random = this.getRandomArbitrary(100, 3000);

      this.setState({
        lastChange: Date.now(),
        timeout: random
      });

      this.changeDistortionValue("distortion", random / 30);
    }

    setTimeout(() => this.generateRandom(), timeout);
  }

  toggleLoop(loopName) {
    let { loop, on } = this.state[loopName];

    on == true ? loop.stop() : loop.start("0m");

    this.setState({
      [`${loopName}`]: {
        loop: loop,
        on: !on
      }
    });
  }

  toggleEffect(effectName) {
    let { effect, wet, on } = this.state[effectName];

    effect.wet.value = on == true ? 0 : wet;
    on = !on;

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    });
  }

  changeEffectWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName];

    effect.wet.value = on == true ? value : 0;
    wet = value;

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    });
  }

  changeDistortionValue(effectName, value) {
    let { effect, wet, on } = this.state.distortion;

    effect.distortion = value;

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    });
  }

  changeFeedbackDelayValue(effectName, value) {
    let { effect, wet, on } = this.state.feedbackDelay;

    effect.maxDelay = value;

    this.setState({
      feedbackDelay: {
        effect,
        wet,
        on
      }
    });
  }

  changeFeedbackDelayWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName];

    effect.wet.value = on == true ? value : 0;
    wet = value;

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    });
  }

  render() {
    let { distortion, synth, loop1, loop2, loop3, loop4 } = this.state;
    let { toggleEffect } = this;

    return (
      <div>
        <div className="LoopContainer">
          <PlaySwitch
            name="play"
            value={loop1.on}
            handleToggleClick={() => this.toggleLoop("loop1")}
          />

          <PlaySwitch
            name="play"
            value={loop2.on}
            handleToggleClick={() => this.toggleLoop("loop2")}
          />

          <PlaySwitch
            name="play"
            value={loop3.on}
            handleToggleClick={() => this.toggleLoop("loop3")}
          />

          <PlaySwitch
            name="play"
            value={loop4.on}
            handleToggleClick={() => this.toggleLoop("loop4")}
          />
        </div>
        <div className="effectsBoard">
          <Distortion
            {...this.state.distortion}
            toggleEffect={() => toggleEffect("distortion")}
            changeEffectWetValue={this.changeEffectWetValue}
            changeDistortionValue={this.changeDistortionValue}
          />
          <FeedbackDelay
            {...this.state.feedbackDelay}
            toggleEffect={() => toggleEffect("feedbackDelay")}
            changeEffectWetValue={this.changeEffectWetValue}
            changeFeedbackDelayValue={this.changeFeedbackDelayValue}
          />
        </div>
      </div>
    );
  }
}
